const db = require('../models/studentModel');

exports.getAllStudents = (req, res) => {
    db.query('SELECT * FROM students', (err, results) => {
        if (err) throw err;
        res.json(results);
    });
};

exports.getStudentById = (req, res) => {
    const id = req.params.id;
    db.query('SELECT * FROM students WHERE id = ?', [id], (err, result) => {
        if (err) throw err;
        res.json(result);
    });
};

exports.createStudent = (req, res) => {
    const { name, email } = req.body;
    db.query('INSERT INTO students (name, email) VALUES (?, ?)', [name, email], (err, result) => {
        if (err) throw err;
        res.json({ id: result.insertId, name, email });
    });
};

exports.updateStudent = (req, res) => {
    const id = req.params.id;
    const { name, email } = req.body;
    db.query('UPDATE students SET name = ?, email = ? WHERE id = ?', [name, email, id], (err, result) => {
        if (err) throw err;
        res.json({ id, name, email });
    });
};

exports.deleteStudent = (req, res) => {
    const id = req.params.id;
    db.query('DELETE FROM students WHERE id = ?', [id], (err, result) => {
        if (err) throw err;
        res.json({ message: 'Student deleted' });
    });
};
